svg.script:

fetch('https://juhan99.github.io/sample/sample.json') // JSON 파일 경로
  .then(response => response.json())
  .then(data => {
    const targetText = data.product1; // JSON에서 문자열 가져오기 (\n 포함)
    const numberTextElement = document.getElementById('numberText');

    // 줄바꿈 포함된 텍스트를 <text>에 삽입
    numberTextElement.textContent = targetText;
  })
  .catch(error => {
    console.error('Error loading JSON:', error);
    const numberTextElement = document.getElementById('numberText');
    numberTextElement.textContent = "Error loading data";
  });
