svg.script:

fetch('https://juhan99.github.io/sample/sample.json')
  .then(response => response.json())
  .then(data => {
    const targetNumber = data.product1; // JSON의 product1 값 가져오기
    const numberTextElement = document.getElementById('numberText');

    let currentNumber = 0; 
    const duration = 2000; 
    const stepTime = 30; 
    const steps = duration / stepTime; 
    const increment = targetNumber / steps; 

    function animateNumber() {
      if (currentNumber < targetNumber) {
        currentNumber += increment; 
        if (currentNumber > targetNumber) currentNumber = targetNumber;
        numberTextElement.textContent = Math.floor(currentNumber) + " Stock"; 
        setTimeout(animateNumber, stepTime); 
      }
    }

    animateNumber();
  })
  .catch(error => {
    console.error('Error loading JSON:', error);
  });
